import React from "react";
import Product from "../components/Product";

export default function ProductScreen(props) {
  const product = data.products.find((x) => x._id === props.match.params.id);
  /* props.match.params.id es un valor que es el mismo que el usuario introduce en esta ruta: <Route path="/product/:id" component={ProductScreen}></Route>, entonces en esa funcion de find, estamos comparando el valor de props.match.params.id con los que hay en el array de products*/
  if (!product) {
    return;
  }
  return (
    <div>
      <div className="row">
        /*col-2 significa que agarre 2 columnas de las 4 disponibles de la
        pantalla para mostrar la imagen del producto, col-1 significa que agarre
        1 columna de las 4 disponibles*/
        <div className="col-2">
          <img src={product.image} alt={product.name} />
        </div>
        <div className="col-1"></div>
        <div className="col-1"></div>
      </div>
    </div>
  );
}
